window.addEventListener("DOMContentLoaded", function() {
	//首頁點擊
	document.querySelector('#Card-Group').addEventListener('click',function(e){
		ga('send', 'event', '首頁點擊', '點擊', 'Card-Group');
	});
    document.querySelector('#Carousel').addEventListener('click',function(e){
		ga('send', 'event', '首頁點擊', '點擊', 'Carousel');
	});
	document.querySelector('#overflow').addEventListener('click',function(e){
		ga('send', 'event', '首頁點擊', '點擊', 'overflow');
	});
	//
	
}, false);

function handleOutboundLinkClicks(event) {
  ga('send', 'event', {
    eventCategory: 'Outbound Link',
    eventAction: 'click',
    eventLabel: event.target.href
  });
}